const notFoundedCar=()=>{carContent.innerHTML+='<div class="notFoundCar">Nie znaleziono takiego samochodu! Wróć do listy naszych samochodów <a href="our-cars.html">Nasze samochody</a></div>'},buildCar=()=>{const carContent=document.getElementById("carContent"),url=new URL(window.location.href),id=url.searchParams.get("id");if(null===id)return void notFoundedCar();const car=cars.find(car=>car.id===id);if(void 0===car)return void notFoundedCar();carContent.innerHTML+=`<img src="assets/our-cars/${car.id}/${car.mainPhoto}" alt="${car.name}">\n    <h3>${car.name}</h3>\n    <ul>\n        <li>Moc: ${car.power.km}KM, ${car.power.nm}NM</li>\n        <li>Silnik: ${car.engine}</li>\n        <li>Przyśpieszenie: 0-100 km/h: ${car.acceleration}s</li>\n        <li>Maksymalna prędkość: ${car.maximumSpeed}km/h</li>\n    </ul>\n    <h3>Cennik</h3>\n    <div class="priceContent">\n        <table id="carPrice">\n            <tr>\n                <td>Liczba dni</td>\n                <td>Cena bez kaucji / doba</td>\n                <td>Cena z kaucją: ${car.deposit} zł</td>\n            </tr>\n        </table>\n        <table id="carKilometerLimit">\n            <tr>\n                <td>Liczba dni</td>\n                <td>Limit kilometrów</td>\n            </tr>\n        </table>\n    </div>\n    <h3>Galeria</h3>\n    <div id="carGallery"></div>`;const carPrice=document.getElementById("carPrice");car.priceList.forEach(price=>{carPrice.innerHTML+=`<tr>\n        <td>${price.numberOfDays}</td>\n        <td>${price.toBeAgreed?"do ustalenia":price.price.toString().concat(" zł")}</td>\n        <td>-${car.discount}%</td>\n        </tr>`});const carKilometerLimit=document.getElementById("carKilometerLimit");car.priceList.forEach(price=>{carKilometerLimit.innerHTML+=`<tr>\n        <td>${price.numberOfDays}</td>\n        <td>${price.kilometerLimit} km</td>\n        </tr>`});const carGallery=document.getElementById("carGallery");car.photos.forEach(photo=>carGallery.innerHTML+=`<img src="assets/our-cars/${car.id}/${photo}" alt="${car.name}">`)};buildCar();