const setCookie=(cookieName,cookieValue,cookieDays)=>{const newDate=new Date;newDate.setTime(newDate.getTime()+24*cookieDays*60*60*1e3);const expires="expires="+newDate.toUTCString();document.cookie=cookieName+"="+cookieValue+";"+expires+";path=/"},checkCookieAcceptCookies=()=>/acceptCookies=true/gm.test(document.cookie);if(!checkCookieAcceptCookies()){document.body.innerHTML+='<section id="cookies">\n    <h2>Zgoda na pliki cookie i dane</h2>\n    <p>Używamy plików cookies na naszej stronie internetowej. Kontynuując korzystanie z naszej strony internetowej, bez zmiany ustawień prywatności przeglądarki, wyrażasz zgodę na przetwarzanie Twoich danych osobowych.</p>\n    <button id="cookieAccept">Akceptuj</button>\n    </section>';const cookieAccept=document.getElementById("cookieAccept");cookieAccept.addEventListener("click",()=>{setCookie("acceptCookies",!0,366),document.getElementById("cookies").remove()})}